'use strict';

module.exports = function (a, b) {

  var arrayA = a.split('.'), arrayB = b.split('.'), totalA, totalB;

  if (arrayA.length < 3 || arrayB.length < 3) {
    throw new Error('Semver syntax error. Should be a string in the exact ' +
      'format of: "x.y.z".');
  }
  totalA = (parseInt(arrayA[0], 10) * 100000000) +
    (parseInt(arrayA[1], 10) * 10000) + parseInt(arrayA[2], 10);
  totalB = (parseInt(arrayB[0], 10) * 100000000) +
    (parseInt(arrayB[1], 10) * 10000) + parseInt(arrayB[2], 10);

  if (totalB > totalA) {
    return true;
  }
  return false;

};
